export default function FeaturesPage() {
  const [t] = useTranslation();
  const { width } = useWindowDimensions();

  const themePrimary = useSelector((state: RootState) => state.theme.primary);
  const themeSecondary = useSelector((state: RootState) => state.theme.secondary);
  const themeType = useSelector((state: RootState) => state.theme.type);

  return (
    <main id="main" className="features-page grid">
      <Head title={t("chat.seo.title")} description={t("chat.seo.description")} />
      <header className="f-w flex a-i-c j-c-c">
        <h2 className={`${width < 769 ? "h3-s f-w__900" : "h1-s"}`}>{t("features.header")}</h2>
      </header>
      <div className="grid">
        {features(themeType === "light" ? themeSecondary : themePrimary).map((array, index) => (
          <ul key={index} className="flex a-i-c j-c-f-s f-f-c-n">
            {array.map((item, itemIndex) => (
              <li key={itemIndex} className="flex a-i-c j-c-c">
                <div className="block grid">
                  {item.img}
                  {/*<div style={{ background: `url(${item.src})` }} />*/}
                  <h2 className={`${width < 769 ? "h5-s" : "h4-s"} flex t-c a-i-c j-c-c f-w__900`}>{t(item.header)}</h2>
                  <p className="h6-s t-j">{t(item.text)}</p>
                </div>
              </li>
            ))}
          </ul>
        ))}
      </div>
    </main>
  );
}
